import React, { useEffect, useState } from 'react';
import { 
  View, 
  Text, 
  StyleSheet, 
  TouchableOpacity, 
  Image, 
  StatusBar,
  Alert
} from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { StackNavigationProp } from '@react-navigation/stack';
import { RootStackParamList } from '../types';
import Button from '../components/Button';
import theme from '../theme/theme';
import { TimeEntryService } from '../services/api';
import NotificationService from '../services/NotificationService';

type HomeScreenNavigationProp = StackNavigationProp<RootStackParamList, 'Home'>;

const HomeScreen: React.FC = () => {
  const navigation = useNavigation<HomeScreenNavigationProp>();
  const [loading, setLoading] = useState<boolean>(true);
  const [resumingSession, setResumingSession] = useState<boolean>(false);

  useEffect(() => {
    // Request notification permissions
    NotificationService.requestPermissions();
    
    // Check if we have a running timer
    checkForRunningTimer();
  }, []);

  const checkForRunningTimer = async () => {
    try {
      setLoading(true);
      const timerState = await TimeEntryService.checkForRunningTimer();
      
      if (timerState.isRunning && timerState.taskId && timerState.startTime) {
        setResumingSession(true);
        // Prompt user if they want to resume the session
        Alert.alert(
          'Timer In Progress',
          'You have a tracking session in progress. Do you want to resume it?',
          [
            {
              text: 'Resume',
              onPress: () => {
                // Find task name
                fetchTaskAndNavigate(timerState.taskId!, timerState.entryId!, timerState.startTime!);
              },
            },
            {
              text: 'Stop',
              style: 'cancel',
              onPress: async () => {
                if (timerState.entryId) {
                  try {
                    // Calculate duration in minutes
                    const startTime = timerState.startTime!;
                    const now = new Date();
                    const durationMinutes = (now.getTime() - startTime.getTime()) / 60000;
                    
                    await TimeEntryService.updateTimeEntry(timerState.entryId, durationMinutes);
                    setResumingSession(false);
                  } catch (error) {
                    console.error('Error stopping timer:', error);
                  }
                }
              },
            },
          ],
          { cancelable: false }
        );
      }
    } catch (error) {
      console.error('Error checking for running timer:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchTaskAndNavigate = async (taskId: number, entryId: number, startTime: Date) => {
    try {
      // For simplicity, we'll just pass the taskId and use a placeholder name
      // In a real app, you would fetch the task details first
      navigation.navigate('Timer', { 
        taskId,
        taskName: 'Current Task' // Placeholder, should be fetched from API
      });
    } catch (error) {
      console.error('Error fetching task:', error);
      Alert.alert('Error', 'Could not resume timer session');
    }
  };

  return (
    <View style={styles.container}>
      <StatusBar barStyle="light-content" backgroundColor={theme.colors.background} />
      
      <View style={styles.header}>
        <Text style={styles.title}>Chrona</Text>
        <Text style={styles.subtitle}>Time Tracker</Text>
      </View>
      
      <View style={styles.logoContainer}>
        <View style={styles.logoCircle}>
          <Text style={styles.logoText}>C</Text>
        </View>
      </View>
      
      <View style={styles.buttonContainer}>
        <Button
          title="Start Tracking"
          onPress={() => navigation.navigate('TaskSelection')}
          variant="primary"
          size="large"
          disabled={loading || resumingSession}
          loading={loading}
          style={styles.button}
        />
        
        <Button
          title="View History"
          onPress={() => Alert.alert('Coming Soon', 'This feature is under development.')}
          variant="outline"
          size="large"
          disabled={loading || resumingSession}
          style={styles.button}
        />
      </View>
      
      <TouchableOpacity 
        style={styles.footer}
        onPress={() => Alert.alert('About', 'Chrona Time Tracker\nVersion 1.0.0')}
      >
        <Text style={styles.footerText}>© 2025 Chrona</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: theme.colors.background,
    padding: theme.spacing.lg,
  },
  header: {
    marginTop: theme.spacing.xl,
    alignItems: 'center',
  },
  title: {
    fontSize: theme.typography.fontSizes.xxxl,
    fontWeight: theme.typography.fontWeights.bold as any,
    color: theme.colors.primary,
    textShadowColor: 'rgba(0, 230, 118, 0.3)',
    textShadowOffset: { width: 0, height: 0 },
    textShadowRadius: 10,
  },
  subtitle: {
    fontSize: theme.typography.fontSizes.lg,
    color: theme.colors.secondaryText,
    marginTop: theme.spacing.xs,
  },
  logoContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  logoCircle: {
    width: 150,
    height: 150,
    borderRadius: 75,
    backgroundColor: theme.colors.card,
    alignItems: 'center',
    justifyContent: 'center',
    borderWidth: 4,
    borderColor: theme.colors.primary,
    ...theme.shadows.lg,
  },
  logoText: {
    fontSize: 80,
    fontWeight: 'bold',
    color: theme.colors.primary,
    textShadowColor: 'rgba(0, 230, 118, 0.3)',
    textShadowOffset: { width: 0, height: 0 },
    textShadowRadius: 10,
  },
  buttonContainer: {
    marginBottom: theme.spacing.xl,
  },
  button: {
    marginVertical: theme.spacing.sm,
    width: '100%',
  },
  footer: {
    alignItems: 'center',
    marginBottom: theme.spacing.sm,
  },
  footerText: {
    color: theme.colors.secondaryText,
    fontSize: theme.typography.fontSizes.sm,
  },
});

export default HomeScreen;
