export const theme = {
  colors: {
    primary: '#00E676',       // Bright green
    primaryDark: '#00C853',   // Darker green
    background: '#121212',    // Dark background (almost black)
    card: '#1E1E1E',          // Card background
    surface: '#2C2C2C',       // Surface elements
    error: '#CF6679',         // Error color
    text: '#FFFFFF',          // Primary text
    secondaryText: '#B3B3B3', // Secondary text
    divider: '#323232',       // Divider color
    success: '#00E676',       // Success indicator
    warning: '#FFD600',       // Warning indicator
    inactive: '#757575',      // Inactive elements
  },
  typography: {
    fontSizes: {
      xs: 12,
      sm: 14,
      md: 16, 
      lg: 18,
      xl: 20,
      xxl: 24,
      xxxl: 32,
    },
    fontWeights: {
      normal: '400',
      medium: '500',
      semibold: '600',
      bold: '700',
    },
  },
  spacing: {
    xs: 4,
    sm: 8,
    md: 16,
    lg: 24,
    xl: 32,
    xxl: 48,
  },
  radius: {
    sm: 4,
    md: 8,
    lg: 16,
    pill: 500,
  },
  shadows: {
    sm: {
      shadowColor: '#000',
      shadowOffset: { width: 0, height: 1 },
      shadowOpacity: 0.22,
      shadowRadius: 2.22,
      elevation: 3,
    },
    md: {
      shadowColor: '#000',
      shadowOffset: { width: 0, height: 2 },
      shadowOpacity: 0.25,
      shadowRadius: 3.84,
      elevation: 5,
    },
    lg: {
      shadowColor: '#000',
      shadowOffset: { width: 0, height: 5 },
      shadowOpacity: 0.36,
      shadowRadius: 6.68,
      elevation: 11,
    },
  },
  ui: {
    buttonHeight: 48,
    inputHeight: 48,
    headerHeight: 56,
  }
};

export type Theme = typeof theme;
export default theme;
