import React, { useEffect, useState } from 'react';
import { 
  View, 
  Text, 
  StyleSheet, 
  FlatList, 
  TouchableOpacity, 
  ActivityIndicator,
  Alert,
  RefreshControl
} from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { StackNavigationProp } from '@react-navigation/stack';
import { RootStackParamList, Task } from '../types';
import Button from '../components/Button';
import theme from '../theme/theme';
import { TaskService } from '../services/api';

type TaskSelectionScreenNavigationProp = StackNavigationProp<RootStackParamList, 'TaskSelection'>;

const TaskSelectionScreen: React.FC = () => {
  const navigation = useNavigation<TaskSelectionScreenNavigationProp>();
  const [tasks, setTasks] = useState<Task[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [refreshing, setRefreshing] = useState<boolean>(false);
  const [selectedTaskId, setSelectedTaskId] = useState<number | null>(null);
  const [loadingMessage, setLoadingMessage] = useState<string>('Loading tasks...');

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      setLoading(true);
      setLoadingMessage('Connecting to server...');
      
      // If using a free tier service like Render, add note about possible delay
      setTimeout(() => {
        if (loading) {
          setLoadingMessage('Waking up the server...\nThis may take up to 30 seconds if the server was asleep.');
        }
      }, 5000);
      
      const fetchedTasks = await TaskService.fetchTasks();
      setTasks(fetchedTasks);
    } catch (error) {
      console.error('Error fetching tasks:', error);
      Alert.alert(
        'Connection Error', 
        'Failed to connect to the server. The server might be starting up or there may be network issues. Would you like to try again?',
        [
          {
            text: 'Try Again',
            onPress: () => fetchTasks()
          },
          {
            text: 'Cancel',
            style: 'cancel'
          }
        ]
      );
    } finally {
      setLoading(false);
      setRefreshing(false);
      setLoadingMessage('Loading tasks...');
    }
  };

  const handleRefresh = () => {
    setRefreshing(true);
    fetchTasks();
  };

  const handleTaskSelect = (task: Task) => {
    setSelectedTaskId(task.id);
  };

  const handleStartTracking = () => {
    if (!selectedTaskId) {
      Alert.alert('Selection Required', 'Please select a task to track');
      return;
    }

    const selectedTask = tasks.find(task => task.id === selectedTaskId);
    if (selectedTask) {
      navigation.navigate('Timer', { taskId: selectedTask.id, taskName: selectedTask.name });
    }
  };

  const handleCreateTask = () => {
    navigation.navigate('CreateTask');
  };

  const renderTaskItem = ({ item }: { item: Task }) => (
    <TouchableOpacity
      style={[
        styles.taskItem,
        selectedTaskId === item.id && styles.taskItemSelected
      ]}
      onPress={() => handleTaskSelect(item)}
    >
      <View style={styles.taskInfo}>
        <Text style={styles.taskName}>{item.name}</Text>
        {item.description && (
          <Text style={styles.taskDescription}>{item.description}</Text>
        )}
      </View>
      {selectedTaskId === item.id && (
        <View style={styles.checkmark}>
          <Text style={styles.checkmarkText}>âœ“</Text>
        </View>
      )}
    </TouchableOpacity>
  );

  const renderEmptyList = () => (
    <View style={styles.emptyContainer}>
      <Text style={styles.emptyText}>No tasks found</Text>
      <Text style={styles.emptySubtext}>Create a task to start tracking time</Text>
      <Button
        title="Create Task"
        onPress={handleCreateTask}
        variant="primary"
        style={styles.emptyButton}
      />
    </View>
  );

  if (loading && !refreshing) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color={theme.colors.primary} />
        <Text style={styles.loadingText}>{loadingMessage}</Text>
        {loadingMessage.includes('Waking up') && (
          <Text style={styles.loadingSubtext}>
            Free tier services go to sleep after inactivity
          </Text>
        )}
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>Select Task</Text>
      </View>

      <FlatList
        data={tasks}
        renderItem={renderTaskItem}
        keyExtractor={item => item.id.toString()}
        contentContainerStyle={styles.listContainer}
        ListEmptyComponent={renderEmptyList}
        refreshControl={
          <RefreshControl
            refreshing={refreshing}
            onRefresh={handleRefresh}
            colors={[theme.colors.primary]}
            progressBackgroundColor={theme.colors.card}
            tintColor={theme.colors.primary}
          />
        }
      />

      <View style={styles.buttonContainer}>
        <Button
          title="Create New Task"
          onPress={handleCreateTask}
          variant="outline"
          style={styles.button}
        />
        <Button
          title="Start Tracking"
          onPress={handleStartTracking}
          variant="primary"
          disabled={!selectedTaskId}
          style={styles.button}
        />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: theme.colors.background,
  },
  header: {
    padding: theme.spacing.lg,
    borderBottomWidth: 1,
    borderBottomColor: theme.colors.divider,
  },
  title: {
    fontSize: theme.typography.fontSizes.xl,
    fontWeight: theme.typography.fontWeights.bold as any,
    color: theme.colors.text,
  },
  loadingContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: theme.colors.background,
    padding: theme.spacing.lg,
  },
  loadingText: {
    marginTop: theme.spacing.md,
    color: theme.colors.text,
    fontSize: theme.typography.fontSizes.md,
    textAlign: 'center',
  },
  loadingSubtext: {
    marginTop: theme.spacing.sm,
    color: theme.colors.secondaryText,
    fontSize: theme.typography.fontSizes.sm,
    textAlign: 'center',
  },
  listContainer: {
    flexGrow: 1,
    padding: theme.spacing.md,
  },
  taskItem: {
    flexDirection: 'row',
    padding: theme.spacing.md,
    backgroundColor: theme.colors.card,
    borderRadius: theme.radius.md,
    marginBottom: theme.spacing.md,
    ...theme.shadows.sm,
  },
  taskItemSelected: {
    backgroundColor: theme.colors.surface,
    borderWidth: 1,
    borderColor: theme.colors.primary,
  },
  taskInfo: {
    flex: 1,
  },
  taskName: {
    fontSize: theme.typography.fontSizes.md,
    fontWeight: theme.typography.fontWeights.semibold as any,
    color: theme.colors.text,
    marginBottom: 4,
  },
  taskDescription: {
    fontSize: theme.typography.fontSizes.sm,
    color: theme.colors.secondaryText,
  },
  checkmark: {
    justifyContent: 'center',
    alignItems: 'center',
    width: 24,
    height: 24,
    borderRadius: 12,
    backgroundColor: theme.colors.primary,
  },
  checkmarkText: {
    color: theme.colors.background,
    fontSize: theme.typography.fontSizes.sm,
    fontWeight: theme.typography.fontWeights.bold as any,
  },
  buttonContainer: {
    padding: theme.spacing.lg,
    borderTopWidth: 1,
    borderTopColor: theme.colors.divider,
  },
  button: {
    marginVertical: theme.spacing.xs,
  },
  emptyContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: theme.spacing.xl,
  },
  emptyText: {
    fontSize: theme.typography.fontSizes.lg,
    fontWeight: theme.typography.fontWeights.medium as any,
    color: theme.colors.text,
    marginBottom: theme.spacing.sm,
  },
  emptySubtext: {
    fontSize: theme.typography.fontSizes.md,
    color: theme.colors.secondaryText,
    textAlign: 'center',
    marginBottom: theme.spacing.lg,
  },
  emptyButton: {
    marginTop: theme.spacing.md,
  },
});

export default TaskSelectionScreen;
