import React, { useState } from 'react';
import { 
  View, 
  Text, 
  StyleSheet, 
  TextInput, 
  TouchableOpacity, 
  Alert, 
  KeyboardAvoidingView, 
  Platform,
  ScrollView
} from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { StackNavigationProp } from '@react-navigation/stack';
import { RootStackParamList } from '../types';
import Button from '../components/Button';
import theme from '../theme/theme';
import { TaskService } from '../services/api';
import MaterialIcons from 'react-native-vector-icons/MaterialIcons';

type CreateTaskScreenNavigationProp = StackNavigationProp<RootStackParamList, 'CreateTask'>;

const CreateTaskScreen: React.FC = () => {
  const navigation = useNavigation<CreateTaskScreenNavigationProp>();
  const [taskName, setTaskName] = useState<string>('');
  const [description, setDescription] = useState<string>('');
  const [loading, setLoading] = useState<boolean>(false);
  const [nameError, setNameError] = useState<string | null>(null);
  
  const validateForm = (): boolean => {
    let isValid = true;
    
    // Validate task name
    if (!taskName.trim()) {
      setNameError('Task name is required');
      isValid = false;
    } else {
      setNameError(null);
    }
    
    return isValid;
  };
  
  const handleSubmit = async () => {
    if (!validateForm()) return;
    
    try {
      setLoading(true);
      
      const result = await TaskService.createTask(
        taskName.trim(), 
        description.trim() || undefined
      );
      
      setLoading(false);
      
      // Show success message
      Alert.alert(
        'Success', 
        'Task created successfully!',
        [
          { 
            text: 'Start Tracking', 
            onPress: () => {
              navigation.navigate('Timer', { 
                taskId: result.id,
                taskName: result.name
              });
            }
          },
          { 
            text: 'Go to Tasks', 
            onPress: () => {
              navigation.navigate('TaskSelection');
            }
          },
        ]
      );
    } catch (error) {
      setLoading(false);
      console.error('Error creating task:', error);
      Alert.alert('Error', 'Failed to create task. Please try again.');
    }
  };
  
  return (
    <KeyboardAvoidingView
      style={styles.container}
      behavior={Platform.OS === 'ios' ? 'padding' : undefined}
      keyboardVerticalOffset={100}
    >
      <ScrollView contentContainerStyle={styles.scrollContainer}>
        <View style={styles.header}>
          <TouchableOpacity 
            style={styles.backButton}
            onPress={() => navigation.goBack()}
          >
            <MaterialIcons name="arrow-back" size={24} color={theme.colors.text} />
          </TouchableOpacity>
          <Text style={styles.title}>Create New Task</Text>
        </View>
        
        <View style={styles.form}>
          <View style={styles.inputGroup}>
            <Text style={styles.label}>Task Name *</Text>
            <TextInput
              style={[
                styles.input,
                nameError && styles.inputError
              ]}
              placeholder="Enter task name"
              placeholderTextColor={theme.colors.secondaryText}
              value={taskName}
              onChangeText={setTaskName}
              autoFocus
              maxLength={50}
            />
            {nameError && (
              <Text style={styles.errorText}>{nameError}</Text>
            )}
          </View>
          
          <View style={styles.inputGroup}>
            <Text style={styles.label}>Description (Optional)</Text>
            <TextInput
              style={[styles.input, styles.textArea]}
              placeholder="Enter task description"
              placeholderTextColor={theme.colors.secondaryText}
              value={description}
              onChangeText={setDescription}
              multiline
              numberOfLines={4}
              maxLength={200}
              textAlignVertical="top"
            />
          </View>
        </View>
        
        <View style={styles.buttonContainer}>
          <Button
            title="Create Task"
            onPress={handleSubmit}
            variant="primary"
            size="large"
            loading={loading}
            disabled={loading}
            style={styles.button}
          />
          
          <Button
            title="Cancel"
            onPress={() => navigation.goBack()}
            variant="outline"
            size="large"
            disabled={loading}
            style={styles.button}
          />
        </View>
      </ScrollView>
    </KeyboardAvoidingView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: theme.colors.background,
  },
  scrollContainer: {
    flexGrow: 1,
  },
  header: {
    flexDirection: 'row',
    alignItems: 'center',
    padding: theme.spacing.lg,
    borderBottomWidth: 1,
    borderBottomColor: theme.colors.divider,
  },
  backButton: {
    marginRight: theme.spacing.md,
  },
  title: {
    fontSize: theme.typography.fontSizes.xl,
    fontWeight: theme.typography.fontWeights.bold as any,
    color: theme.colors.text,
  },
  form: {
    padding: theme.spacing.lg,
  },
  inputGroup: {
    marginBottom: theme.spacing.lg,
  },
  label: {
    fontSize: theme.typography.fontSizes.md,
    fontWeight: theme.typography.fontWeights.medium as any,
    color: theme.colors.text,
    marginBottom: theme.spacing.sm,
  },
  input: {
    height: theme.ui.inputHeight,
    backgroundColor: theme.colors.card,
    borderRadius: theme.radius.md,
    borderWidth: 1,
    borderColor: theme.colors.divider,
    color: theme.colors.text,
    paddingHorizontal: theme.spacing.md,
    fontSize: theme.typography.fontSizes.md,
  },
  textArea: {
    height: 120,
    paddingTop: theme.spacing.md,
  },
  inputError: {
    borderColor: theme.colors.error,
  },
  errorText: {
    color: theme.colors.error,
    fontSize: theme.typography.fontSizes.sm,
    marginTop: theme.spacing.xs,
  },
  buttonContainer: {
    padding: theme.spacing.lg,
    paddingTop: 0,
    marginTop: 'auto',
  },
  button: {
    marginBottom: theme.spacing.md,
  },
});

export default CreateTaskScreen;
